package javaBasics;

public class StringConcatanation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	System.out.println("String concatenation will always happens from Left to right");
	System.out.println("+ operator is used to concatenate 2 Strings");
	
		int a=10;
		int b=20;
		
		String x = "Hello";
		String y = "Vamsi";
		
			System.out.println(a+b);
			System.out.println(x+y);
			
			System.out.println(a+b+x+y);
			System.out.println(x+y+a+b);
			System.out.println(x+y+(a+b));
			
		//println - is used to print on the console with a new line
		System.out.println(x);
		System.out.println(y);
		
		//print - is used to print on the console
		System.out.print(x);
		System.out.print(y);
		
		

	}

}
